-- ========================================
-- 01_DATABASE_SETUP.sql
-- ========================================
-- Complete database setup for Opine Agora SC
-- Execute this FIRST in your Supabase SQL Editor

-- 1. Drop existing tables to avoid conflicts
DROP TABLE IF EXISTS comments CASCADE;
DROP TABLE IF EXISTS ads CASCADE;
DROP TABLE IF EXISTS posts CASCADE;

-- 2. Create posts table
CREATE TABLE posts (
    id BIGINT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
    title VARCHAR(255) NOT NULL,
    slug VARCHAR(255) UNIQUE NOT NULL,
    excerpt TEXT,
    content TEXT,
    featured BOOLEAN DEFAULT FALSE,
    status VARCHAR(20) DEFAULT 'draft' CHECK (status IN ('draft', 'published')),
    category VARCHAR(50) NOT NULL,
    tags TEXT[] DEFAULT '{}',
    author VARCHAR(100) NOT NULL,
    date TIMESTAMP WITH TIME ZONE DEFAULT CURRENT_TIMESTAMP,
    image VARCHAR(500),
    meta_title VARCHAR(255),
    meta_description TEXT,
    meta_keywords TEXT,
    created_at TIMESTAMP WITH TIME ZONE DEFAULT CURRENT_TIMESTAMP,
    updated_at TIMESTAMP WITH TIME ZONE DEFAULT CURRENT_TIMESTAMP
);

-- 3. Create comments table
CREATE TABLE public.comments (
  id bigint generated by default as identity not null,
  post_id bigint null,
  name character varying(100) not null,
  email character varying(255) not null,
  content text not null,
  status character varying(20) null default 'pending'::character varying,
  created_at timestamp with time zone null default CURRENT_TIMESTAMP,
  updated_at timestamp with time zone null default CURRENT_TIMESTAMP,
  constraint comments_pkey primary key (id),
  constraint comments_email_time_unique unique (email, post_id, created_at),
  constraint comments_post_id_fkey foreign KEY (post_id) references posts (id) on delete CASCADE,
  constraint comments_status_check check (
    (
      (status)::text = any (
        (
          array[
            'pending'::character varying,
            'approved'::character varying,
            'rejected'::character varying
          ]
        )::text[]
      )
    )
  )
) TABLESPACE pg_default;

-- 4. Create ads table
CREATE TABLE ads (
    id BIGINT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
    title VARCHAR(255) NOT NULL,
    slug VARCHAR(255) UNIQUE NOT NULL,
    content TEXT,
    category VARCHAR(50) NOT NULL CHECK (category IN ('banner', 'sidebar', 'footer', 'popup')),
    status VARCHAR(20) DEFAULT 'draft' CHECK (status IN ('draft', 'approved')),
    image_url VARCHAR(500),
    link_url VARCHAR(500),
    start_date TIMESTAMP WITH TIME ZONE,
    end_date TIMESTAMP WITH TIME ZONE,
    created_at TIMESTAMP WITH TIME ZONE DEFAULT CURRENT_TIMESTAMP,
    updated_at TIMESTAMP WITH TIME ZONE DEFAULT CURRENT_TIMESTAMP
);

-- 5. Create performance indexes
CREATE INDEX IF NOT EXISTS idx_posts_status ON public.posts using btree (status) TABLESPACE pg_default;
CREATE INDEX IF NOT EXISTS idx_posts_category ON public.posts using btree (category) TABLESPACE pg_default;
CREATE INDEX IF NOT EXISTS idx_posts_date ON public.posts using btree (date) TABLESPACE pg_default;
CREATE INDEX IF NOT EXISTS idx_posts_featured ON public.posts using btree (featured) TABLESPACE pg_default;
CREATE INDEX IF NOT EXISTS idx_comments_post_id ON public.comments using btree (post_id) TABLESPACE pg_default;
CREATE INDEX IF NOT EXISTS idx_comments_status ON public.comments using btree (status) TABLESPACE pg_default;
CREATE INDEX IF NOT EXISTS idx_comments_email ON public.comments using btree (email) TABLESPACE pg_default;
CREATE INDEX IF NOT EXISTS idx_comments_created_at ON public.comments using btree (created_at) TABLESPACE pg_default;
CREATE INDEX IF NOT EXISTS idx_ads_status ON public.ads using btree (status) TABLESPACE pg_default;
CREATE INDEX IF NOT EXISTS idx_ads_category ON public.ads using btree (category) TABLESPACE pg_default;

-- 6. Create utility functions
CREATE OR REPLACE FUNCTION generate_slug(title TEXT) RETURNS TEXT AS $$
BEGIN
    RETURN lower(regexp_replace(regexp_replace(unaccent(title), '[^a-zA-Z0-9\s-]', '', 'g'), '\s+', '-', 'g'));
END;
$$ LANGUAGE plpgsql;

CREATE OR REPLACE FUNCTION update_updated_at_column()
RETURNS TRIGGER AS $$
BEGIN
    NEW.updated_at = CURRENT_TIMESTAMP;
    RETURN NEW;
END;
$$ LANGUAGE plpgsql;

-- 7. Create triggers for updated_at
CREATE TRIGGER update_posts_updated_at BEFORE UPDATE ON posts FOR EACH ROW EXECUTE FUNCTION update_updated_at_column();
CREATE TRIGGER update_comments_updated_at BEFORE UPDATE ON comments FOR EACH ROW EXECUTE FUNCTION update_updated_at_column();
CREATE TRIGGER update_ads_updated_at BEFORE UPDATE ON ads FOR EACH ROW EXECUTE FUNCTION update_updated_at_column();

